const fs = require('fs')

const allOldDistances = JSON.parse(fs.readFileSync('distances.json', 'utf-8'))


function dijkstra(graph: Record<string, string[]>, ...starts: string[]) {
	const sKey = starts.join('-').replace(/|/g, '_')
	const path = `distances.json`
	const jGraph = JSON.stringify(graph)
	if (!(jGraph in allOldDistances)) {
		allOldDistances[jGraph] = {}
	}
	const graphOldDistances = allOldDistances[jGraph]
	if (sKey in graphOldDistances) {
		return graphOldDistances[sKey]
	}
	const distances: Record<string, number> = {};
	for (const node in graph) {
		distances[node] = Number.MAX_SAFE_INTEGER;
	}
	for (const start of starts) {
		distances[start] = 0;
	}

	const unvisited = new Set(Object.keys(graph));
	while (unvisited.size) {
		let node = null;
		for (const otherNode of unvisited) {
			if (!node || distances[otherNode] < distances[node]) node = otherNode
		}
		if (node === null) break;

		unvisited.delete(node);

		for (const neighbor of graph[node]) {
			distances[neighbor] = Math.min(distances[neighbor], distances[node] + 1)
		}
	}
	graphOldDistances[sKey] = distances
	fs.writeFileSync(path, JSON.stringify(allOldDistances, null, 2))

	return distances;
}

function solveQuest(notes: string) {
	const world = notes.split('\n').map(l => [...l]);

	const start = { r: 1, c: 0 }

	const trees = [];
	for (let r = 0; r < world.length; r++) {
		for (let c = 0; c < world[r].length; c++) {
			if (world[r][c] === 'P') {
				trees.push({ r, c })
			}
		}
	}

	const graph = {}
	for (let r = 0; r < world.length; r++) {
		for (let c = 0; c < world[r].length; c++) {
			if (world[r][c] !== '.' && world[r][c] !== 'P') {
				continue
			}
			const fromKey = r + '|' + c
			if (!(fromKey in graph)) graph[fromKey] = []
			for (const [ro, co] of [[1, 0], [-1, 0], [0, 1], [0, -1]]) {
				const [nr, nc] = [r + ro, c + co]
				if (world[nr]?.[nc] === '.' || world[nr]?.[nc] === 'P') {
					const toKey = nr + '|' + nc
					graph[fromKey].push(toKey)
				}
			}
		}
	}

	const distances = dijkstra(graph, start.r + '|' + start.c);
	let furthest = Number.MIN_SAFE_INTEGER
	for (const tree of trees) {
		furthest = Math.max(furthest, distances[tree.r + '|' + tree.c])
	}
	return furthest
}

