function solveQuest3(notes: string) {
	const world = notes.split('\n').map(l => [...l]);

	const starts = [{ r: 1, c: 0 }, { r: world.length - 2, c: world[0].length - 1 }]

	const trees = [];
	for (let r = 0; r < world.length; r++) {
		for (let c = 0; c < world[r].length; c++) {
			if (world[r][c] === 'P') {
				trees.push({ r, c })
			}
		}
	}

	const flowable = '.P'

	const graph = {}
	for (let r = 0; r < world.length; r++) {
		for (let c = 0; c < world[r].length; c++) {
			if (!flowable.includes(world[r][c])) {
				continue
			}
			const fromKey = r + '|' + c
			if (!(fromKey in graph)) graph[fromKey] = []
			for (const [ro, co] of [[1, 0], [-1, 0], [0, 1], [0, -1]]) {
				const [nr, nc] = [r + ro, c + co]
				if (flowable.includes(world[nr]?.[nc])) {
					const toKey = nr + '|' + nc
					graph[fromKey].push(toKey)
				}
			}
		}
	}
	const summedDistances: Record<string, number> = {}

	for (const [t, tree] of trees.entries()) {
		console.log(t / trees.length * 100)
		const distances = dijkstra(graph, tree.r + '|' + tree.c)
		for (const loc in distances) {
			summedDistances[loc] = (summedDistances[loc] ?? 0) + distances[loc]
		}
	}

	const avgDistances: Record<string, number> = { ...summedDistances }
	for (const loc in avgDistances) {
		avgDistances[loc] = avgDistances[loc] / trees.length
	}

	const allLocs = Object.entries(avgDistances).sort((a, b) => a[1] - b[1]).filter(entry => !trees.some(t => t.r + '|' + t.c === entry[0]))
	//const allLocs = Object.entries(summedDistances).sort((a, b) => a[1] - b[1])
	const digLocs = allLocs.filter((entry) => entry[0] === allLocs[0][0]).map(entry => entry[0])
	let best = Number.MAX_SAFE_INTEGER
	for (const loc of digLocs) {
		const distances = dijkstra(graph, loc);
		let total = 0
		for (const tree of trees) {
			total += distances[tree.r + '|' + tree.c]
		}
		best = Math.min(best, total)
	}
	return best
}

